package com.demo.MySql.service;import com.demo.MySql.model.Employee;import com.demo.MySql.repository.EmployeeRepo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class EmployeeService {    @Autowired    EmployeeRepo repo;    public List<Employee> getEmployees(){        return repo.findAll();    }}